}

@mixin tablet() {
    @media screen and (min-width:481px) {
        @content
    }
}

@mixin laptop() {
    @media screen and (min-width:769px) {
        @content
    }
}
@mixin desktop() {
    @media screen and (min-width:1024px) {
        @content
    }
}

