sources.sass/components/_buttons.scss:
@use '../abstract/var' as var;

.button > a {
    font-family: bebas-neue-pro;
    font-size: 1.2rem;
    font-weight: 600;
}

.button {
	pointer-events: auto;
	cursor: pointer;
	background: map-get(var.$colors, negro);
	border: none;
	padding: 1.5rem 3rem;
	margin: 1.4em 0;
	font-family: inherit;
	font-size: inherit;
	position: relative;
	display: inline-block;
	box-shadow:
            2.8px 2.8px 2.2px rgba(0, 0, 0, 0.02),
            6.7px 6.7px 5.3px rgba(0, 0, 0, 0.028),
            12.5px 12.5px 10px rgba(0, 0, 0, 0.035),
            22.3px 22.3px 17.9px rgba(0, 0, 0, 0.042),
            41.8px 41.8px 33.4px rgba(0, 0, 0, 0.05),
            100px 100px 80px rgba(0, 0, 0, 0.07);
	transition: all .5s ease;
	z-index: 1;
}
.button:hover {
	box-shadow: none;
}

.button::before,
.button::after {
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
}

.button--hyperion {
	font-family: input-mono-narrow, monospace;
	font-weight: 500;
	padding: 1rem 1.5rem;
	border: 1px solid map-get(var.$colors, negro);
	overflow: hidden;
	color: map-get(var.$colors, verde);
}

.button--hyperion span {
	display: block;
	position: relative;
}

.button--hyperion > a > span {
	overflow: hidden;
}

.button--hyperion > a > span > span {
	overflow: hidden;
	mix-blend-mode:difference;
}

.button--hyperion:hover > a > span > span {
	animation: MoveUpInitial 0.2s forwards, MoveUpEnd 0.2s forwards 0.2s;
}

@keyframes MoveUpInitial {
	to {
		transform: translate3d(0,-105%,0);
	}
}

@keyframes MoveUpEnd {
	from {
		transform: translate3d(0,100%,0);
	}
	to {
		transform: translate3d(0,0,0);
	}
}

.button--hyperion::before {
	content: '';
	background: map-get(var.$colors, verde);
	transition: transform 0.3s cubic-bezier(0.7, 0, 0.2, 1);
	transform-origin: 100% 50%;
}

.button--hyperion:hover::before {
	transform: scale3d(0,1,1);
	transform-origin: 0% 50%;
}

sources.sass/components/_marquee.scss:
@use '../abstract/var' as var;
@use '../abstract/mixins' as mix;

/*
=============================================
MARQUEE 1
=============================================
*/
.section-3__part-2,
.footer__marquee-2 {
    font-family: 'bebas-neue-pro', sans-serif;
    color: #111;
    height: 80vh;
    display: flex;
    justify-content: center;
    align-items: center;
}
.footer__marquee-2 {
  height: 40vh;
  @include mix.desktop() {
    height: 60vh;
  }
}

:root {
    --marquee-width: 80vw;
    --marquee-height: 30vh;
    /* --marquee-elements: 12; */ /* defined with JavaScript */
    --marquee-elements-displayed: 0.8;
    --marquee-element-width: calc(var(--marquee-width) / var(--marquee-elements-displayed));
    --marquee-animation-duration: calc(var(--marquee-elements) * 12s);
  }

  .marquee {
    width: var(--marquee-width);
    height: var(--marquee-height);
    background-color: map-get(var.$colors, negro);
    color: map-get(var.$colors, verde);
    overflow: hidden;
    position: relative;
  }
  .marquee-2 {
    background-color: map-get(var.$colors, verde);
    color: map-get(var.$colors, negro);
  }
  .marquee:before, .marquee:after {
    position: absolute;
    top: 0;
    width: 10rem;
    height: 100%;
    content: "";
    z-index: 1;
  }
  .marquee:before {
    left: 0;
    background: linear-gradient(to right, #2C2C2C 0%, transparent 100%);
  }
  .marquee:after {
    right: 0;
    background: linear-gradient(to left, #2C2C2C 0%, transparent 100%);
  }
  .marquee-2:before {
    left: 0;
    background: linear-gradient(to right, #DBFF00 0%, transparent 100%);
  }
  .marquee-2:after {
    right: 0;
    background: linear-gradient(to left, #DBFF00 0%, transparent 100%);
  }
  .marquee-content {
    list-style: none;
    height: 100%;
    display: flex;
    animation: scrolling var(--marquee-animation-duration) linear infinite;
  }
  /* .marquee-content:hover {
    animation-play-state: paused;
  } */
  @keyframes scrolling {
    0% { transform: translateX(0); }
    100% { transform: translateX(calc(-1 * var(--marquee-element-width) * var(--marquee-elements))); }
  }
  .marquee-content h2 {
    display: flex;
    justify-content: center;
    align-items: center;
    /* text-align: center; */
    flex-shrink: 0;
    width: var(--marquee-element-width);
    max-height: 100%;
    font-size: min(max(3.4rem, 14vw), 20rem);
    white-space: nowrap;
    border-top: 1px solid map-get(var.$colors, verde);
    border-bottom: 1px solid map-get(var.$colors, verde);
  }


  @media (max-width: 600px) {

    :root {
      --marquee-width: 100vw;
      --marquee-height: 16vh;
      --marquee-elements-displayed: 0.36;
    }
    .marquee:before, .marquee:after { width: 5rem; }
  }

sources.sass/layout/_header.scss:
@use '../abstract/var' as var;
@use '../abstract/mixins' as mix;

/*
=============================================
HEADER
=============================================
*/

.header {
    position: fixed;
    z-index: 2;
    top: 0;
    left: 0;
    right: 0;
    width: 100%;
    background-color: map-get(var.$colors, blanco);
    padding: .4em 0;
    border-bottom: map-get(var.$colors, negro) 1px solid;
    color: map-get(var.$colors, negro);
    transition: background-color .5s ease;

    &--active {
        background-color: map-get(var.$colors, negro);
        color: map-get(var.$colors, verde);
        overflow: hidden;
    }
    .flex {
        @include mix.flex(row, wrap, space-between, center);
    }

    &__left-side {
        @include mix.flex(row, nowrap, flex-end, center);
    }

    &__coleccion {
        display: none;
        @include mix.tablet() {
            display: block;
            font-size: 1.4rem;
            margin-right: 1em;
            opacity: 1;
            transition: opacity .5s ease;

            &--active {
                opacity: 0;
            }
        }
    }

    /* BOTÃ“N MENÃš */
    &__hamburger-menu {
        width: 2.4em;
        height: 2.4em;
        background-color: map-get(var.$colors, negro);
        border: map-get(var.$colors, verde) 1.5px solid;
        border-radius: 50%;
        @include mix.flex(column, nowrap, center, center);
        cursor: pointer;
        transition: all .5s ease;

        &--active {
            background-color: map-get(var.$colors, verde);
            @include mix.desktop() {
                width: 4em;
                height: 4em;
            }
        }
    }

    &__bar {
        width: 1.2rem;
        height: 3px;
        background-color: map-get(var.$colors, verde);
        position: relative;
        transition: background-color .5s ease;
    }
    &__bar::before,
    &__bar::after {
        content: "";
        position: absolute;
        width: inherit;
        height: inherit;
        background-color: map-get(var.$colors, verde);
        transition: all .5s ease;
    }
    &__bar::before {
        top: .4rem;
    }
    &__bar::after {
        top: -.4rem;
    }
    .open &__bar {
        background-color: transparent;
    }
    .open &__bar::before {
        top: 0;
        transform: rotate(135deg);
        background-color: map-get(var.$colors, negro);
        @include mix.desktop() {
            width: 2rem;
            left: -8px;
        }

    }
    .open &__bar::after {
        top: 0;
        transform: rotate(-135deg);
        background-color: map-get(var.$colors, negro);
        @include mix.desktop() {
            width: 2rem;
            left: -8px;
        }
    }

    /* Nav */
    &__nav {
        width: 100%;
        @include mix.flex(column, nowrap, center, center);
        opacity: 0;
        height: 0vh;
        overflow: hidden;
        transition: all .5s ease;

        &--active {
            opacity: 1;
            height: 90vh;
        }
    }
    &__li {
        margin: 0.9em 0;
        width: 100vw;
        background-color: map-get(var.$colors, verde);
        transform: rotate(-8deg);
    }
    &__li:nth-child(even) {
        background-color: map-get(var.$colors, negro);
        border-top: map-get(var.$colors, verde) 1px solid;
        border-bottom: map-get(var.$colors, verde) 1px solid;
    }
    &__li:nth-child(odd) {
        color: map-get(var.$colors, negro);
    }
    &__a {
        text-align: center;
        font-family: bebas-neue-pro, sans-serif;
        font-size: 1.6rem;
        font-weight: 600;
        @include mix.tablet() {
            font-size: 3rem;
        }
        @include mix.desktop() {
            font-size: 4rem;
            transition: all .5s ease;
        }
    }
    &__a:hover {
        @include mix.desktop() {
            width: 100vw;
            -webkit-text-stroke: 1.2px map-get(var.$colors, negro);
            -webkit-text-fill-color: map-get(var.$colors, verde);
            font-size: 5rem;
        }
    }
    /* Nav social */
    &__nav-social-list {
        @include mix.flex(row, nowrap, center, center);
        @include mix.laptop() {
            margin-top: 1em;
        }
    }
    &__nav-social-item {
        margin: 0 0.9em;
    }
    &__nav-social-link {
        font-size: 1.2rem;
        color: map-get(var.$colors, verde);
        transition: transform .5s ease;
    }
    &__nav-social-link:hover {
        transform: scale(1.5);
    }
}

.headroom {
    will-change: transform;
    transition: transform 200ms linear;
}
.headroom--pinned {
    transform: translateY(0%);
}
.headroom--unpinned {
    transform: translateY(-100%);
}

sources.sass/layout/_footer.scss:
@use '../abstract/var' as var;
@use '../abstract/mixins' as mix;

/*
=============================================
FOOTER
=============================================
*/
.footer {
    background-color: map-get(var.$colors, verde);
    padding: 4em 0;
    @include mix.desktop() {
        padding: 12em 0 6em 0;
    }
    &__title {
        max-width: 200px;
        line-height: 0.9;
        -webkit-text-stroke: 1px map-get(var.$colors, negro);
        -webkit-text-fill-color: map-get(var.$colors, verde);
        position: relative;
        @include mix.desktop() {
            max-width: 500px;
            -webkit-text-stroke: 1.6px;
        }
    }
    &__title::before {
        content: "";
        position: absolute;
        top: -20px;
        left: 0;
        width: 200px;
        height: 1px;
        background-color: map-get(var.$colors, negro);
        @include mix.laptop() {
            width: 400px;
        }
    }
    &__part-2 {
        @include mix.desktop() {
            position: relative;
            @include mix.flex(row, nowrap, space-between, center);
            justify-content: flex-start;
        }   
    }
    &__contacto {
        @include mix.laptop() {
            flex-basis: 40%;
        }
    }
    &__nav {
        @include mix.laptop() {
            flex-basis: 20%;
            align-self: flex-start;
            position: relative;
        }
    }
    &__nav::before {
        @include mix.desktop() {
            content: "";
            position: absolute;
            top: 0;
            left: -40px;
            width: 1px;
            height: 400px;
            background-color: map-get(var.$colors, negro);
        }
    }
    &__logo {
        max-width: 100px;
    }
    &__social-list {
        @include mix.flex(row, nowrap, flex-start, center);
        padding: 1.4em 0;
    }
    &__social-item {
        margin-right: 1.4em;
        font-size: 1.2rem;
        transition: transform .5s ease;
    }
    &__social-item:hover {
        @include mix.desktop() {
            transform: scale(1.8);
        }
    }
    &__ph {
        margin: 1.2em 0;
    }

    &__w-icon {
        @include mix.flex(row, nowrap, flex-start, center);
        margin: 1em 0;
    }
    &__w-icon i {
        font-size: 4rem;
        margin-right: 0.2em;
        transition: transform .5s ease;
    }
    &__w-icon i:hover {
        @include mix.desktop() {
            transform: scale(1.3);
        }
    }
    &__cel {
        font-size: 1.4rem;
    }
    &__nav-title {
        color: map-get(var.$colors, negro);
        font-family: bebas-neue-pro;
        font-weight: 600;
        font-size: 2rem;
        position: relative;
        margin: 1em 0 0.4em 0;
        @include mix.desktop() {
            margin: 0 0 0.4em 0 ;
        }
    }
    &__nav-title::before {
        content: "";
        position: absolute;
        top: 45px;
        left: 0;
        width: 200px;
        height: 1px;
        background-color: map-get(var.$colors, negro);
    }
    &__nav-item {
        color: map-get(var.$colors, negro);
        font-family: bebas-neue-pro;
        font-weight: 400;
        font-size: 1.6rem;
        margin: 0.6em 0;
        transition: transform .5s ease;
    }
    &__nav-item:hover {
        @include mix.desktop() {
            transform: translateX(20px);
        }
    }
}

sources.sass/pages/_home.scss:
@use '../abstract/var' as var;
@use '../abstract/mixins' as mix;

/*
=============================================
HERO SECTION
=============================================
*/
.hero {
    width: 100%;
    padding-top: 6em;
    background-color: map-get(var.$colors, blanco);
    @include mix.desktop() {
        padding-top: 12em;
    }
    &__wrapper {
        text-align: center;
    }
    &__text {
        padding: 1em 0;
        @include mix.desktop() {
            padding: 2em 0;
        }
    }
    &__title {
        box-shadow:
            2.8px 2.8px 2.2px rgba(0, 0, 0, 0.02),
            6.7px 6.7px 5.3px rgba(0, 0, 0, 0.028),
            12.5px 12.5px 10px rgba(0, 0, 0, 0.035),
            22.3px 22.3px 17.9px rgba(0, 0, 0, 0.042),
            41.8px 41.8px 33.4px rgba(0, 0, 0, 0.05),
            100px 100px 80px rgba(0, 0, 0, 0.07);
        background-color: map-get(var.$colors, verde);
        transform: rotate(-10deg);
        padding: 0.1em 0;
        color: map-get(var.$colors, negro);
    }
    &__text {
        background-color: map-get(var.$colors, verde);
    }
}
/*
=============================================
SECTION 1
=============================================
*/
.section-1 {
    z-index: 999;
    background-color: map-get(var.$colors, blanco);
    padding-bottom: 3em;
    @include mix.tablet() {
        padding-bottom: 6em;
    }
    @include mix.desktop() {
    padding-bottom: 14em;
    }

    &__phbig {
        display: flex;
        justify-content: center;
        align-items: center;
        padding: 4em 0;
        @include mix.desktop() {
            padding: 10em 0;
            line-height: 1.1
        }
    }
    &__ph2 {
        max-width: 24em;
        text-align: center;
    }

    &__jumbo {
        position: relative;
        padding: 4em 0;
        @include mix.desktop() {
            padding: 12em 0;
        }
    }
    &__title,
    &__title-2 {
        text-align: center;
        // -webkit-text-stroke: 1px map-get(var.$colors, negro);
        // -webkit-text-fill-color: map-get(var.$colors, blanco);
    }
    &__title::before {
        content: "";
        position: absolute;
        top: -10px;
        left: 0;
        width: 2.2em;
        height: 1px;
        background-color: map-get(var.$colors, negro);
        @include mix.desktop() {
            top: -20px;
        }
    }
    &__girl-basket {
        @include mix.desktop() {
            @include mix.flex(row, nowrap, space-evenly, flex-star);

        }
    }
    &__girl-text {
        @include mix.desktop() {
            flex-basis: 40%;
        }

    }
    &__girl-title {
        -webkit-text-stroke: 1px map-get(var.$colors, negro);
        -webkit-text-fill-color: map-get(var.$colors, blanco);
        @include mix.desktop() {
            margin: 0;
            line-height: 0.8;
            -webkit-text-stroke: 2px;
        }
    }
    &__ph {
        margin: 1.6em 0;
        @include mix.tablet() {
            max-width: 30rem;
        }
    }

    &__girl-img {
        @include mix.desktop() {
            flex-basis: 50%;
        }
    }
}
/*
=============================================
SECTION 2
=============================================
*/
.section-2 {
    z-index: -1;
    width: 100%;
    background: radial-gradient(circle, rgba(198,1,1,1) 0%, rgba(193,0,0,1) 20%, rgba(112,1,1,1) 78%);
    &__wrapper {
        position: relative;
        padding: 5em 0 0 0;
        @include mix.tablet() {
            padding: 7em 0 0 0;
        }
        @include mix.desktop() {
            @include mix.flex(row, nowrap, space-evenly, center);
            padding: 12em 0 0 0;
        }
    }
    &__wrapper::before {
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        width: 1px;
        height: 100px;
        background-color: white;
        @include mix.desktop() {
            height: 600px;
        }
    }
    &__text {
        color: map-get(var.$colors, blanco);
        @include mix.desktop() {
            flex-basis: 30%;
            text-align: right;
        }
    }
    &__img {
        opacity: 0;
        transition: opacity 3s;
        @include mix.desktop() {
            order: -1;
            flex-basis: 60%;
        }
    }
    .trans-1 {
        opacity: 1;
    }
    &__ph {
        margin: 2em 0 1em 0;
        @include mix.desktop() {
            margin: 2em 0 2em 4em;
        }
    }
}
/*
=============================================
LOCO SECTION
=============================================
*/
.git {
    background-color: map-get(var.$colors, negro);
    flex-direction: column;
    overflow: hidden;
    padding-bottom: 4.5em;
    @include mix.desktop() {
        padding-bottom: 12em;
    }

    &__text {
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: column;
    }

    &__title,
    &__title--dos,
    &__title--tres,
    &__title--cuatro {
        font-size: clamp(1rem, 8vw, 12rem);
        font-family: bebas-neue-pro, sans-serif;
        text-align: center;
        background-color: map-get(var.$colors, verde);
        color: map-get(var.$colors, negro);
        padding: 1rem 2rem;
        text-transform: uppercase;
        white-space: nowrap;
        display: inline-block;
    }
    &__title--dos {
        transform: rotate(12deg);
    } 
    &__title--cuatro {
        transform: rotate(-12deg);
    } 
}
/*
=============================================
SECTION 3
=============================================
*/
.section-3 {
    background-color: map-get(var.$colors, negro);
    &__wrapper {
        position: relative;
    }
    &__wrapper::before {
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        width: 1px;
        height: 100px;
        background-color: map-get(var.$colors, verde);
        @include mix.desktop() {
            top: 280px;
            height: 600px;
        }
    } 
    &__part-1 {
        @include mix.desktop() {
            @include mix.flex(row, nowrap, space-evenly, center);
        }
    }
    &__left {
        @include mix.desktop() {
            flex-basis: 45%;
        }
    }
    &__title {
        color: map-get(var.$colors, verde);
        margin-left: 8px;
        @include mix.desktop() {
            margin-left: 0;
        }
    }
    &__part-2 {
        display: flex;
    }
    &__right {
        @include mix.flex(column, nowrap, flex-end, center);
        @include mix.desktop() {
            margin-top: 10em;
            flex-basis: 50%;
        }
    }
    &__ph {
        color: map-get(var.$colors, blanco);
        max-width: 500px;
        text-align: center;
        margin: 2em 0;
        @include mix.tablet() {
            margin: 4em 0;
        }
    }
}
/*
=============================================
SECTION 4
=============================================
*/
.section-4 {
    width: 100%;
    background-color: map-get(var.$colors, verde);
    padding: 3em 0;
    @include mix.tablet() {
        padding: 6em 0;
    }
    @include mix.desktop() {
        padding: 12em 0;
    }
    &__part-1 {
        @include mix.desktop() {
            @include mix.flex(row, nowrap, space-between, flex-star);
        }
    }
    &__text {
        flex-basis: 40%;
        @include mix.desktop() {
            order: 2;
            text-align: right;
        }
    }
    &__title {
        position: relative;
        -webkit-text-stroke: 1px map-get(var.$colors, negro);
        -webkit-text-fill-color: map-get(var.$colors, verde);
        @include mix.desktop() {
            order: 2;
            text-align: right;
            -webkit-text-stroke: 2px;
        }
    }
    &__title::before {
        content: "";
        position: absolute;
        top: -20px;
        right: 0;
        width: 2.2em;
        height: 1px;
        background-color: map-get(var.$colors, negro);
        @include mix.desktop() {
            top: -50px;
            right: 0;
        }
    }
    &__img {
        flex-basis: 50%;
    }
    &__ph {
        margin: 2em 0;
        @include mix.desktop() {
            margin: 2em 0 2em 7em;
        }
    }
    &__part-2 {
        position: relative;
        margin-top: 2em;
        @include mix.tablet() {
            margin-top: 6em;
        }
    }
    &__line {
        width: 1px;
        height: 100px;
        background-color: map-get(var.$colors, negro);
        position: absolute;
        left: 10px;
        @include mix.tablet() {
            height: 200px;
            left: 20px;
        }
        @include mix.desktop() {
            height: 600px;
            left: 70px;
        }
    }
    &__title-2 {
        font-size: min(max(2rem, 10vw), 16rem);
        opacity: 0.2;
        max-width: 150px;
        position: absolute;
        top: 20px;
        left: 20px;
        @include mix.tablet() {
            left: 40px;
        }
        @include mix.desktop() {
            top: 150px;
            left: 150px;
        }
    }
}
/*
=============================================
PRODUCTOS
=============================================
*/
.productos {
    width: 100%;
    background-color: map-get(var.$colors, blanco);
    padding-top: 3em;
    @include mix.desktop() {
        padding-top: 8em;
    }
    &__text {
        text-align: center;
        padding: 4em 0;
        position: relative;
        @include mix.tablet() {
            padding: 7em 0;
        }
        @include mix.desktop() {
            padding: 12em 0;
        }
    }
    &__text::before {
        content: "";
        position: absolute;
        top: 55px;
        left: 0;
        width: 200px;
        height: 1px;
        background-color: map-get(var.$colors, negro);
        @include mix.tablet() {
            top: 100px;
            width: 400px;
        }
        @include mix.desktop() {
            top: 200px;
            width: 700px;
        }
    }
    &__title {
        margin-bottom: .3em;
        opacity: 0;
        transition: opacity 3s;
        @include mix.desktop() {
            margin-bottom: 0;
        }
    }
    .opac-0 {
        opacity: 1;
    }
    &__subtitle {
        opacity: 0;
        transition: opacity 4s;
    }
    .opac-1 {
        opacity: 1;
    }
    &__list {
        display: grid;
        gap: 1.6em;
        padding-bottom: 4em;
        grid-template-columns: repeat(auto-fill, minmax(min(100%, 22rem), 1fr));
        @include mix.desktop() {
            grid-template-columns: repeat(auto-fill, minmax(min(100%, 23rem), 1fr));
            padding-bottom: 12em;
            gap: 4em;
        }
    }
    &__item {
        background-color: map-get(var.$colors, blanco);
        border: map-get(var.$colors, negro) 1px solid;
        padding: 0.6em;
        transition: all .5s ease;
        @include mix.tablet() {
            padding: 1em;
        }
    }
    &__item:hover {
        @include mix.desktop() {
        transform: translate(-20px, -20px);
        background-color: map-get(var.$colors, blanco);
        box-shadow:
            2.8px 2.8px 2.2px rgba(0, 0, 0, 0.02),
            6.7px 6.7px 5.3px rgba(0, 0, 0, 0.028),
            12.5px 12.5px 10px rgba(0, 0, 0, 0.035),
            22.3px 22.3px 17.9px rgba(0, 0, 0, 0.042),
            41.8px 41.8px 33.4px rgba(0, 0, 0, 0.05),
            100px 100px 80px rgba(0, 0, 0, 0.07);
            padding: 0;
            border: none;
        }
    }
    &__item:hover > h6 {
        color: red;
    }
    &__item img:hover {
        mix-blend-mode:luminosity;
    }
    &__item-title {
        margin-top: 0.2em;   
    }
}
